const initialState = {  //원본 초기값
   user : {
    isLoggedIN : false,
    user2: null,
    signUpData: {},
    loginData : {},
   },
   post:{
    mainPosts:[]
   }
};

export const loginAction = (data)=>{           //  action 데이터를 바꾸고 싶을때 값을 따로만들어둔다
    return {
        type :'LOG_IN',
        data,
    }
}

export const logoutAction = (data)=>{       
    return {
        type :'LOG_IN',
    }
}


const changeNickname2 =(data) => {    //  action 데이터를 바꾸고 싶을때 
    return {
        type : 'CHANGE_NICKNAME',
        data,
    }
};

//  action 데이터를 바꾸고 싶을때 
// const changeNickname = {    
//     type : 'CHANGE_NICKNAME',
//     data : 'boogicho',
// }

                            //( 이전상태   , 액션)   => 다음상태
const rootReducer = (state = initialState, action) => {
    switch (action.type){
        case 'LOG_IN':
          return {
            ...state,
            user:{
                ...state.user,
                isLoggedIn :true,
                user:action.data,
            },
          }
          case 'LOG_OUT':
            return {
              ...state,
              user:{
                  ...state.user,
                  isLoggedIn :false,
                  user:null,
              },
            }    
    }
}

export default rootReducer;
